import { useRef, useEffect } from "react";
export const useDisallowStrictMode = () => {
    const ref = useRef({ initialized: false });
    useEffect(() => {
        // with `[]`, this should only ever run once.
        if (ref.current.initialized) {
            // react strict mode is enabled.
            throw new Error("Waas detected a double render.\n\n1. useWalletContext() cannot be used with React.StrictMode. Please remove <React.StrictMode /> from your hierarchy. (e.g `App.tsx` should not contain a React.StrictMode tag).\n\n2. If this is in debug, your app may have re-rendered midframe. This is OK.");
        }
        ref.current.initialized = true;
        // this should never run again.
    }, []);
};
