export class InMemoryTokenPersistenceStrategy {
    token;
    validUntil;
    constructor() {
        this.token = undefined;
    }
    get() {
        return this.token && this.validUntil
            ? {
                contents: this.token,
                validUntil: this.validUntil,
            }
            : undefined;
    }
    update(token, ttlS) {
        this.token = token;
        this.validUntil = new Date(Date.now() + ttlS * 1000);
    }
    clear() {
        this.token = undefined;
        this.validUntil = undefined;
    }
}
